polishDate.code:
function polishDate(parameters) {
	if (typeof parameters !== 'object' || parameters === null) return undefined;

	Object.entries(parameters).forEach(([key, value]) => {
		if (key.endsWith("_at")) parameters[key] = iml.parseDate(value, 'YYYY-MM-DD HH:mm:ss');

		if(Array.isArray(value)) {
			parameters[key] = value.map(p => iml.polishDate(p));
		}

		if (typeof value === 'object' && value !== null) {
			parameters[key] = iml.polishDate(value);
		}
	});

	return parameters;
}
