function buildMailSendBody(parameters) {

	if(parameters.custom_args !== null && parameters.custom_args !== undefined) {
		let custom_args = {}
		parameters.custom_args.map(item => {
			custom_args[item['key']] = item['value'];
		})
		parameters.custom_args = custom_args
	}

	//PERSONALIZATIONS array of objects
	parameters.personalizations = [];
	parameters.personalizations[0] = {};
	parameters.personalizations[0].to = parameters.send_to;
	parameters.personalizations[0].cc = parameters.copy_recipient;
	parameters.personalizations[0].bcc = parameters.blind_copy_recipient;
	if(parameters.dynamic_template_data){

		parameters.dynamic_template_data.forEach(obj => {

			try {
				const DTvalue = JSON.parse(obj.value);
				obj.value = DTvalue;
			} catch (e) {}         
		});

		parameters.personalizations[0].dynamic_template_data = iml.toCollection(parameters.dynamic_template_data, 'key', 'value')

	}
	if(parameters.send_at){
		parameters.personalizations[0].send_at = Number(iml.formatDate(parameters.send_at, 'X'));
	}

	if(parameters.dynamic_template_dataJSON){
		parameters.personalizations[0].dynamic_template_data = JSON.parse(parameters.dynamic_template_dataJSON);
	}

	delete parameters.send_at;
	delete parameters.send_to;
	delete parameters.copy_recipient;
	delete parameters.blind_copy_recipient;
	delete parameters.dynamic_template_data;
	delete parameters.dynamic_template_dataJSON;
	if(iml.length(parameters.personalizations[0].cc) === 0){
		delete parameters.personalizations[0].cc;
	}

	//CONTENT array of objects
	parameters.content = [];
	parameters.content[0] = {};
	parameters.content[0].type = parameters.content_type;
	parameters.content[0].value = parameters.content_value;

	delete parameters.content_type;
	delete parameters.content_value;
	delete parameters.templateOrNot;

	//ATTACHMENTS
	for (i = 0; i < iml.length(parameters.attachments); i++){
		parameters.attachments[i].content = iml.base64(parameters.attachments[i].content);
		if(parameters.attachments[i].content_id){
			parameters.attachments[i].disposition = 'inline';
		}else{
			parameters.attachments[i].disposition = 'attachment';
		}
	}

	//HEADERS
	let o = {};

	if(parameters.headers){
		parameters.headers.filter(pair => pair.key).forEach(pair => o[pair.key] = pair.value);

		parameters.headers = o;
	}

	return parameters;
}

