function buildContactFieldsNew(body) {
    let customFields = [];
    let outputArr = [];

    outputArr.unshift(
        {
            name: 'list_ids',
            type: 'select',
            label: 'List',
            options: 'rpc://getAllNewLists',
            multiple: true
        },
        {
            name: 'email',
            type: 'email',
            label: 'Email',
            required: true,
            validate: { max: 254},
            help: 'Contact with the same email will be updated.'
        }
    );

    body.reserved_fields.forEach(reservedFields => {
        if(reservedFields.read_only || reservedFields.name === 'email') return;

        let reservedObj = {
            name: reservedFields.name,
            label: iml.startcase(iml.replace(reservedFields.name, '_', ' '))
        };

        switch(reservedFields.field_type){
            case 'Text':
            case 'Number':
            case 'Date':
                reservedObj.type  = iml.lower(reservedFields.field_type);
                break;
            default:
                reservedObj.type = 'text';
        }

        if(reservedFields.name === 'alternate_emails'){
            reservedObj.type = 'array',
            reservedObj.spec = { type: 'text', label: 'Email'},
            reservedObj.validate = {maxItems: 5}
        }

        outputArr.push(reservedObj);
    })

    outputArr.push(
        {
            name: 'id',
            type: 'select',
            label: 'Contact ID',
            options: 'rpc://getAllContacts',
            advanced: true,
            mode: 'edit',
            help: 'Use only if you wish to update the main email of the contact.'
        }
    ); //put the id as last parameter in the array

    if(body.custom_fields){
        body.custom_fields.forEach(fields => {
            let obj = {};
            obj.name = fields.id;
            obj.label = fields.name;

            switch(fields.field_type){
                case 'Text':
                case 'Number':
                case 'Date':
                    obj.type  = iml.lower(fields.field_type);
                    break;
                default:
                    obj.type = 'text';
            }

            customFields.push(obj);
        });
    }       

    if(customFields.length > 0){
        outputArr.push(
            {
                name: 'custom_fields',
                label: 'Custom Fields',
                type: 'collection',
                spec: customFields
            }
        );
    }

    return outputArr;
}

