function transformResponseDates(obj, timestampDateKeys) {
	// timestampDateKeys are the object keys that need to be transformed from timestamp to date object

	var keys = iml.keys(obj);

	var output = {}
	let keysLength = keys.length;
	for (var i = 0; i < keysLength; i++) {
		var value = obj[keys[i]];

		if (value && timestampDateKeys && timestampDateKeys.includes(keys[i])) {
			value = iml.parseDate(value, 'X');
		}

		output[keys[i]] = value;
	}

	return output;
}
