function filter(filter, order) {

    if(!filter) {
        if(order) return order;
        return;
    }

    let string = filter.map(or => {
        return or.map(and => {
            if(!(and.a)||!(and.b)||!(and.o)) return;

            if(and.b instanceof Date) {
                and.b = iml.formatDate(and.b, 'YYYY-MM-DD HH:mm:ss');
            }

            return `${and.a}${and.o}${and.b}`
        }).join('^');
    }).join('^OR');

    if(order) {
        if(!string) return order;

        string = string + '^' + order;
    }

    return string;
}

