function tableFields(fields, interface) {

    if (!fields) return;

    let arr = [];

    fields.forEach(item => {

        let obj = {};

        if (!interface) {
            if (item.name.startsWith('sys_')) return;
        }

        obj.name = item.name;
        obj.label = iml.if(item.label === 'Sys ID', 'Record ID', iml.capitalize(item.label));

        switch (item.internalType) {
            /* case 'reference':
            case 'glide_list':
            case 'journal':
            case 'journal_list':
            case 'domain_path':
            case 'html':
            case 'choice':
            case 'timer':
            case 'journal_input':
            case 'string':
            case 'domain_id':
                if (item.exampleValue && item.exampleValue !== '') {
                    obj.help = 'e.g. ' + `**${item.exampleValue}**`;
                }
                obj.type = 'text';
                break; */
            case 'glide_date_time':
            case 'glide_date':
            case 'glide_duration':
            case 'glide_time':
            case 'glide_utc_time':
            case 'due_date':
            case 'calendar_date_time':
            case 'schedule_date_time':
            case 'Date/Time':
                obj.type = 'date';
                if (item.exampleValue && item.exampleValue !== '') {
                    obj.help = 'e.g. ' + `**${item.exampleValue}**`;
                }
                break;
            case 'boolean':
                obj.type = 'boolean';
                break;
            case 'integer':
            case 'float':
            case 'price':
            case 'currency':
            case 'decimal':
            case 'number':
                obj.type = 'number';
                if (item.exampleValue && item.exampleValue !== '') {
                    obj.help = 'e.g. ' + `**${item.exampleValue}**`;
                }
                break;

            case 'integer':
            case 'days_of_week':
            case 'longint':
            case 'long':
            case 'int':
            case 'month_of_year':
                obj.type = 'integer';
                break;

            default:
                obj.type = 'text';
                if (item.exampleValue && item.exampleValue !== '') {
                    obj.help = 'e.g. ' + `**${item.exampleValue}**`;
                }
                break;

        }

        arr.push(obj);

    });

    if (!interface) {

        arr = arr.sort((a, b) => (a.label > b.label) ? 1 : ((b.label > a.label) ? -1 : 0));
        return {
            name: "fields",
            label: "Fields",
            type: "collection",
            spec: arr
        };
    }

    return arr;

}

