function fieldsRequest(fields) {

    if(!fields) return;

    if(Array.isArray(fields)) {
        fields.forEach(item => {
            if(item.value instanceof Date) {
                item.value = iml.formatDate(item.value, 'YYYY-MM-DD HH:mm:ss')
            }
            else item.value = item.value.toString();
        });
        fields = iml.toCollection(fields, 'key', 'value');
    }
    else {
        for (let [key, value] of Object.entries(fields)) {
            if(value instanceof Date) {
                fields[key] = iml.formatDate(value, 'YYYY-MM-DD HH:mm:ss')
            }
            else fields[key] = value.toString();
        }
    }

    return fields;

}

