function filterResponse(body, trigger) {

    if(!body) return;
    let arr = [];

    body.forEach(item => {
        if(trigger) {
            if(item.name === 'sys_created_on' || item.name === 'sys_updated_on') return;
        }
        let obj = {
            label: iml.if(item.name === 'sys_id', 'Record ID', item.label),
            value: item.name
        };
        arr.push(obj);
    });

    arr = arr.sort((a,b) => (a.label > b.label) ? 1 : ((b.label > a.label) ? -1 : 0)); 

    return arr;

}

