function generalResponse(obj, params) {

    if(!obj) return;

    if(params && params.sysparm_display_value) return obj; // returns values in 'Display' mode, not the database values

    for (let [key, value] of Object.entries(obj)) {
        if (typeof value === 'object' && !(value instanceof Date) && !(Array.isArray(value))) {
            if(obj[key]['value']) {
                obj[key] = obj[key]['value'];
            }
        }
        else if (/^\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}/gm.test(value)) {
            obj[key] = iml.parseDate(value);
        }
        else if (!isNaN(value)) {
            obj[key] = parseFloat(value);
        }
        else if (value === 'true') obj[key] = true;
        else if (value === 'false') obj[key] = false;
    }

    return obj;

}

codeChallengeEncode.code:
function codeChallengeEncode(text) {
    if (typeof text !== 'string') throw new Error('Invalid text for encoding.');

    text = iml.sha256(text, 'base64');
    text = iml.replace(text, '+', '-');
    text = iml.replace(text, '/', '_');
    text = iml.replace(text, '=', '');
