function handleError(statusCode, body) {
    if (typeof body === "string") {
		const errorCodes = {...iml.errorCodes(), ...iml.errorCodes2()}

        if (body.indexOf("ERROR:") !== -1) {
			const [, errorCode] = body.split(':')
			return `${body} - ${errorCodes[errorCode]}`
        }
    }

    const message =
        (body && body.errors && body.errors.length && body.errors[0].message) ||
        body.message;
    if (message) return `[${statusCode}] ${message}`;
    const messages = {
        400: "The request could not be understood by the server due to incorrect syntax. The client SHOULD NOT repeat the request without modifications.",
        401: "The limit has been exceeded.",
        403: "Unauthorized request. The client does not have access rights to the content. Unlike 401, the clients identity is known to the server.",
        404: "The server can not find the requested resource.",
        405: "The request HTTP method is known by the server but has been disabled and cannot be used for that resource.",
        406: "The server doesnt find any content that conforms to the criteria given by the user agent in the `Accept` header sent in the request.",
        408: "Indicates that the server did not receive a complete request from the client within the servers allotted timeout period.",
        409: "The request could not be completed due to a conflict with the current state of the resource.",
        410: "The requested resource is no longer available at the server.",
        411: "The server refuses to accept the request without a defined Content- Length. The client MAY repeat the request if it adds a valid Content-Length header field.",
        413: "Request entity is larger than limits defined by server.",
        415: "The mediatype in Content-type of the request is not supported by the server.",
        422: "Unprocessible entity",
        500: "Internal server error. Please try again later or contact Siteglide support if error persists.",
        502: "The server got an invalid response while working as a gateway to get a response needed to handle the request.",
        503: "The server is not ready to handle the request.",
        504: "The server is acting as a gateway and cannot get a response in time for a request.",
    };
    return messages[statusCode]
        ? `[${statusCode}] ${messages[statusCode]}`
        : `[${statusCode}] Bad request.`;
}

