function parseDates(obj) {
    obj.order_date = obj.order_date ? iml.parseDate(obj.order_date) : undefined;
    if (obj.order) {
        obj.order.date = obj.order.date ? iml.parseDate(obj.order.date) : undefined;
        obj.order.date_iso8601 = obj.order.date_iso8601 ? iml.parseDate(obj.order.date_iso8601) : undefined;
    }
    return obj;
}

