function getInput(params, type = 'insert') {
	if (params.tags && params.tags.length) {
		params.tags = params.tags.join(',')
	} else {
		delete params.tags
	}
	delete params.reblog
	delete params.blog_id
	delete params.id

	const files = []

	;(params.content || []).forEach((block, i) => {
		if (block.type === 'image') {
			;(block.media || []).forEach((media, i) => {
				if (media.origin === 'file') {
					media.identifier = 'image' + files.length
					files.push({
						identifier: media.identifier,
						filename: media.filename,
						data: media.data
					})
					delete media.filename
					delete media.data
				}
				delete media.origin
				if (!Object.keys(media.poster).length) {
					delete media.poster
				}
			})
		}
	})

	if (type === 'insert') {
		const response = {
			json: JSON.stringify(params)
		}
		files.forEach(file => {
			response[file.identifier] = {
				value: file.data,
				options: {
					filename: file.filename
				}
			}
		})
		return response
	} else {
		return params
	}
}

