function getBefore(posts, currentCountResponses, maxCountResponses) {
	if (currentCountResponses >= maxCountResponses) {
		return false
	}
	if (!posts || !posts.length) {
		return false
	}
	const last = posts[posts.length - 1]
	const date = last.date.getTime() / 1000
	return date
}

