function getOldInput(params) {
	delete params.blogId
	if (params.tags && params.tags.length) {
		params.tags = params.tags.join(',')
	} else {
		delete params.tags
	}

	if (params.date && typeof params.date === 'object' && params.date.toISOString) {
		params.date = params.date.toISOString()
	}

	if (params.data) {
		params.data = {
			value: params.data,
            options: {
				filename: params.fileName
            }
        }
		delete params.fileName
		delete params.audioType
	}
	if (params.data64) {
		params.data64 = params.data64.toString('base64')
	}
	if (params.photos) {
		params.photos.forEach((photo, key) => {
			params[`data[${key}]`] = {
				value: photo.data,
				options: {
					filename: photo.fileName
				}
			}
		})
		delete params.photos
	}
	return params
}

