getErrorMessage.code:
function getErrorMessage(body, statusCode) {
	if (!body || !body.errors) {
		return `Error: ${statusCode}.`
	}
	if (body.response && body.response.errors) {
		return `${statusCode}: ${body.response.errors}` 
	}
	return body.errors.map(e => {
		return `${e.title} (${e.code}): ${e.detail}`
	}).join(' ')
}

