{
	"info": {
		"url": "https://www.wrike.com/api/v3/contacts?me=true",
		"headers": {
			"Authorization": "Bearer {{connection.accessToken}}"
		},
		"response": {
			"uid": "{{body.data[0].id}}",
			"metadata": {
				"type": "text",
				"value": "{{body.data[1].firstName}} {{body.data[1].lastName}}"
			}
		},
		"log": {
			"sanitize": ["request.headers.authorization"]
		}
	},
	"token": {
		"url": "https://www.wrike.com/oauth2/token",
		"body": {
			"code": "{{temp.code}}",
			"client_id": "{{ifempty(data.clientId, common.clientId)}}",
			"grant_type": "authorization_code",
			"redirect_uri": "{{oauth.redirectUri}}",
			"client_secret": "{{ifempty(data.clientSecret, common.clientSecret)}}"
		},
		"type": "urlencoded",
		"method": "POST",
		"response": {
			"data": {
				"expires": "{{addSeconds(now, body.expires_in)}}",
				"accessToken": "{{body.access_token}}",
				"refreshToken": "{{body.refresh_token}}"
			}
		},
		"log": {
			"sanitize": ["request.body.code", "request.body.client_secret", "response.body.access_token", "response.body.refresh_token"]
		}
	},
	"refresh": {
		"url": "https://www.wrike.com/oauth2/token",
		"body": {
			"client_id": "{{ifempty(parameters.clientId, common.clientId)}}",
			"grant_type": "refresh_token",
			"client_secret": "{{ifempty(parameters.clientSecret, common.clientSecret)}}",
			"refresh_token": "{{data.refreshToken}}"
		},
		"type": "urlencoded",
		"method": "POST",
		"response": {
			"data": {
				"expires": "{{addSeconds(now, body.expires_in)}}",
				"accessToken": "{{body.access_token}}",
				"refreshToken": "{{body.refresh_token}}"
			}
		},
		"condition": "{{data.expires < addMinutes(now, 15)}}",
		"log": {
			"sanitize": ["request.body.client_secret", "request.body.refresh_token", "response.body.access_token", "response.body.refresh_token"]
		}
	},
	"authorize": {
		"qs": {
			"scope": "{{join(oauth.scope, ',')}}",
			"client_id": "{{ifempty(parameters.clientId, common.clientId)}}",
			"redirect_uri": "{{oauth.redirectUri}}",
			"response_type": "code"
		},
		"url": "https://www.wrike.com/oauth2/authorize",
		"response": {
			"temp": {
				"code": "{{query.code}}"
			}
		}
	}
}

