function buildCFI(data) {
	const out = []
	for(var key in data){
		out.push({type: "text", name: data[key].id, label: data[key].title})
	}
	return out
}

buildDates.code:
function buildDates(dates) {
	let keepUndef = true;
	if(dates.start !== null && dates.start !== "" && dates.start !== undefined){
		dates.start = iml.formatDate(dates.start, "YYYY-MM-DDTHH:mm:ss");
		keepUndef = false;
	}
	else{
		dates.start = null;
	}
	if(dates.due !== "" && dates.due !== null && dates.due !== undefined){
		dates.due = iml.formatDate(dates.due, "YYYY-MM-DDTHH:mm:ss");
		keepUndef = false;
	}
	else{
		dates.due = null
	}
	if(!(dates.duration !== "" && dates.duration !== undefined && dates.duration !== null)){
		dates.duration = null
	}
	if(keepUndef){
		return undefined
	}
	return JSON.stringify(dates);
}

customFieldsInterface.code:
function customFieldsInterface(body) {
	var fields = body.customFields;
	var newFields = {}
	for(var field in fields){
		var newField = fields[field].value
		var newKey = fields[field].id
		newFields[newKey] = newField
	}
	body.customFields = newFields;
	return body
}
