listRecentCampaigns.apiJsonc:
{
	"url": "/recentcampaigns",
	"method": "GET",
	"qs": {
		"resfmt": "JSON",
		"status": "{{parameters.status}}",
		"sort": "asc",
		"fromindex": 1,
		"range": "{{min(parameters.limit, 100)}}"
	},
	"response": {
		"limit": "{{parameters.limit}}",
		"valid": {
			"condition": "{{if(body.code, body.code = '0', true)}}",
			"message": "Error {{body.code}}: {{ifempty(body.message, body.error)}}",
			"type": "RuntimeError"
		},
		"iterate": "{{body.recent_campaigns}}",
		"output": {
			"{{...}}": "{{omit(item, 'created_time_gmt', 'updated_time_gmt', 'sent_time_gmt', 'created_date_string', 'updated_date_string', 'sent_date_string')}}",
			"created_time": "{{parseDate(item.created_time_gmt / 1000, 'X')}}",
			"updated_time": "{{if(item.updated_time_gmt, parseDate(item.updated_time_gmt / 1000, 'X'), undefined)}}",
			"sent_time": "{{if(item.sent_time_gmt, parseDate(item.sent_time_gmt / 1000, 'X'), undefined)}}"
		}
	},
	"pagination": {
		"condition": "{{body.total_record_count > (pagination.page - 1) * 100}}",
		"qs": {
