function parseDateV2(input, format, timezone, ...keys) {
	if (Array.isArray(input)) return input.map(i => iml.parseDateV2(i, format, timezone, ...keys));
	if (typeof input === 'object' && input !== null) {
		Object.entries(input).forEach(([k, v]) => {
			if (keys.includes(k) && typeof v === "string") {
				input[k] = iml.parseDate(v, format, timezone);
				return;
			}
			if (typeof v === 'object' && input !== null) {
				input[k] = iml.parseDateV2(v, format, timezone, ...keys);
			}
		});
		return input;
	}
	return undefined;
}

parseFields.code:
function parseFields(fields) {
	if (!Array.isArray(fields)) return undefined;
	return fields
		.sort((a, b) => {
			if (a.block_sequence < b.block_sequence) return -1;
			if (a.block_sequence > b.block_sequence) return 1;
			return a.SEQUENCE - b.SEQUENCE;
		}).map(field => {
			let base = {
				"name": field.DISPLAY_NAME,
				"label": field.DISPLAY_NAME,
				"required": field.IS_MANDATORY
			}
			switch (field.UITYPE) {
				case "textarea":
					base.multiline = true;
				case "text":
					base.type = "text";
					if (field.MAXLENGTH) base.validate = { max: field.MAXLENGTH };
					break;

				case "email":
					base.type = "email";
					break;

				case "date":
					base.type = "date";
					break;

				default:
					base.type = "text";
			}
			return base;
		});
}

