function removeNull(src) {
	if (src && typeof src === 'object') {
		Object.keys(src).forEach(key => {
			if (src[key] == null) {
				delete src[key];
			} else if (typeof src[key] === 'object') {
				iml.removeNull(src[key]);
			}
		});
	}
	return src;
}

buildFilter.code:
function buildFilter(filter) {
	filter = filter[0];
	var out = "";
	for(var k in filter){
		var field = filter[k].a;
		var operator = filter[k].o;
		var value = filter[k].b;
		if (iml.containsChar(value, [" ", "@", "-", ".", "_"]) || /[a-z]|[A-Z]/gm.test(value)) {
			value = '"' + value + '"';
		}
		out += field + operator + value + "&&";
	}
	return out.substring(0, out.length - 2);
}

