function triggerEpoch(body,select) {

	let arr = [];

	body.forEach(item => {

		let obj = {};
		obj.label = "Record no. "+ (body.indexOf(item) + 1);
		obj.date = iml.switch(select, 'create', iml.parseDate(item.Added_Time, 'DD-MMM-YYYY HH:mm:ss'), 'modify', iml.parseDate(item.Modified_Time, 'DD-MMM-YYYY HH:mm:ss'));

		arr.push(obj);
	});
	return arr;

}

