function parseDateTime(time) {

    if(/\d*-\d*-\d* \d*:\d*:\d*/gm.test(time)){
        time = iml.parseDate(time, 'YYYY-MM-DD HH:mm:ss')
    }
    else if (/\d*-\w*-\d* \d*:\d*:\d*/gm.test(time)) {
        time = iml.parseDate(time, 'DD-MMM-YYYY HH:mm:ss')
    }
    else {
        time = iml.parseDate(time);
    }
    return time;
}

