function recordResponse(item) {

	if (item.Added_Time) {
		if(/\d*-\d*-\d* \d*:\d*:\d*/gm.test(item.Added_Time)){
			if(/\d\d-\d\d-\d\d \d\d:\d\d:\d\d/gm.test(item.Added_Time)) {
				item.Added_Time = iml.parseDate(item.Added_Time, 'DD-MM-YY HH:mm:ss');
			}
			else item.Added_Time = iml.parseDate(item.Added_Time, 'YYYY-MM-DD HH:mm:ss');
		}
		else if (/\d*-\w*-\d* \d*:\d*:\d*/gm.test(item.Added_Time)) {
			item.Added_Time = iml.parseDate(item.Added_Time, 'DD-MMM-YYYY HH:mm:ss');
		}
		else {
			item.Added_Time = iml.parseDate(item.Added_Time);
		}
	}
	if (item.Modified_Time) {
		if(/\d*-\d*-\d* \d*:\d*:\d*/gm.test(item.Modified_Time)){
			if(/\d\d-\d\d-\d\d \d\d:\d\d:\d\d/gm.test(item.Modified_Time)) {
