function extractFormFields(body, shouldReturnArray, trigger, update) {
	if (!body || !body.response) return null;

	let transformedFormFields = [];

	body.response.fields.forEach((field) => {
		const result = iml.transformFormField(field, shouldReturnArray, update);
		if (result === null) return;
		if (Array.isArray(result)) {
			result.forEach(item => transformedFormFields.push(item));
		}
		else {
			transformedFormFields.push(result);
		}
	});

	if (!shouldReturnArray) {
		return [{
			name: 'form_fields',
			label: 'Form Fields',
			type: 'collection',
			spec: transformedFormFields
		}];
	}

	if(trigger) {
		transformedFormFields.push(
			{
				name: "Added_Time",
				label: "Added Time",
				type: "date"
			},
			{
				name: "Modified_Time",
				label: "Modified Time",
				type: "date"
			}
		)
	}

	return transformedFormFields;
}

