function formatOutputParams(params) {
	let form_fields = params.form_fields;

	for (var key in form_fields) {
		if (form_fields[key] instanceof Array) {
			form_fields[key] = form_fields[key].join();
		} else if (form_fields[key] instanceof Date) {
			form_fields[key] = iml.formatDate(form_fields[key], "DD-MMM-YYYY HH:mm:ss");
		}
	}
	return form_fields;
}

