function transformFormField(field, shouldReturnArray, update) {
	if (!field || !field.type) return;

	var obj = {};
	obj.name = field.fieldname;
	obj.label = field.displayname;
	obj.help = field.tooltip;
	if(!update) {
		obj.required = field.required;
	}
	obj.editable = true;
	//obj.default = field.Initial;
	if (field.maxchar) {
		obj.validate = {};
		obj.validate.max = field.maxchar;
	}

	switch (field.type) {
		case 1:
			obj.type = "text";
			obj.multiline = false;
			break;
		case 2: // Multi Line
		case 4: // Rich Text
			obj.type = "text";
			obj.multiline = true;
			break;
		case 3:
			obj.type = "email";
			break;
		case 5: // Integer
			obj.type = "integer";
			if (field.maxchar && field.maxchar === parseInt(field.maxchar, 10)) {
				obj.validate = {};
				obj.validate.max = iml.getMaxNumberForNumDigits(field.maxchar);
			}
			break;
		case 6:
			obj.type = "number";
			if (field.maxchar && field.maxchar === parseInt(field.maxchar, 10)) {
				obj.validate = {};
				obj.validate.max = iml.getMaxNumberForNumDigits(field.maxchar);
			}
			break;
		case 7: // Percent
			obj.type = "integer";
			if (field.maxchar && field.maxchar === parseInt(field.maxchar, 10)) {
				obj.validate = {};
				obj.validate.max = iml.getMaxNumberForNumDigits(field.maxchar);
			}
			obj.label += " (%)";
			break;
		case 8:
			obj.type = "number";
			if (field.maxchar && field.maxchar === parseInt(field.maxchar, 10)) {
				obj.validate = {};
				obj.validate.max = iml.getMaxNumberForNumDigits(field.maxchar);
			}
			obj.label += " (" + field.CurrencyType + ")";
			break;
		case 10: // Date
			obj.type = "date";
			obj.time = false;
			break;
		case 11: // Date Time
			obj.type = "date";
			obj.time = true;
			break;
		case 12: // Dropdown + Lookup
		case 13: // Radio Buttons
			obj.type = "select";
			obj.options = field.choices.map(key => {
				var option = {};
				option.value = key.value
				option.label = key.key;
				return option;
			});
			break;
		case 14: // Multi Select
		case 15: // Check Boxes
			obj.type = "select";
			obj.options = field.choices.map(key => {
				var option = {};
				option.value = key.value
				option.label = key.key;
				return option;
			});
			obj.multiple = true;
			break;
		case 16: // Decision Box
			obj.type = "boolean";
			obj.default = (field.initial === "checked");
			break;
		case 17:
			obj.type = "url";
			break;
		case 27: // phone number
			obj.type = 'text';
			break;
		case 29: // Name
			if (!shouldReturnArray) {
				let arr = [];
				if (field.subfields[0].prefix === true) {
					let obj1 = {};
					obj1.name = field.fieldname + '.' + field.subfields[0].labelname;
					obj1.label = field.subfields[0].displayname;
					obj1.type = 'select';
					obj1.options = field.subfields[0].choices.map(key => {
						var option = {};
						option.value = key.value
						option.label = key.key;
						return option;
					});
					if (field.required === true) {
						if(!update) obj1.required = true
					}
					arr.push(obj1);
				}
				if (field.subfields[1].first_name === true) {
					let obj2 = {};
					obj2.name = field.fieldname + '.' + field.subfields[1].labelname;
					obj2.label = field.subfields[1].displayname;
					obj2.type = 'text';
					if (field.required === true) {
						if(!update) obj2.required = true
					}
					arr.push(obj2);
				}
				if (field.subfields[2].last_name === true) {
					let obj3 = {};
					obj3.name = field.fieldname + '.' + field.subfields[2].labelname;
					obj3.label = field.subfields[2].displayname;
					obj3.type = 'text';
					if (field.required === true) {
						if(!update) obj3.required = true
					}
					arr.push(obj3);
				}
				if (field.subfields[3].suffix === true) {
					let obj4 = {};
					obj4.name = field.fieldname + '.' + field.subfields[3].labelname;
					obj4.label = field.subfields[3].displayname;
					obj4.type = 'text';
					if (field.required === true) {
						if(!update) obj4.required = true
					}
					arr.push(obj4);
				}
				return arr;
			}
			else {
				obj.type = 'text';
				break;
			}

		case 30: // Address
			if (!shouldReturnArray) {
				let arr1 = [];
				arr1 = iml.addressParameters(field);
				return arr1;
			}
			else {
				obj.type = 'text';
				break;
			}

		default:
			obj = null;
			break;
	}

	return obj;
}

