function prepareRecordForCreateOrUpdate(params, authToken, scope, criteria) {
	var obj = iml.formatOutputParams(params);
	var auth = {};
	auth.authtoken = authToken;
	auth.scope = scope;
	obj = iml.merge(obj, auth);

	if (criteria) {
		var update = {};
		update.criteria = criteria;
		obj = iml.merge(obj, update);
		obj.process_until_limit = true;
		obj.submit = "Update Record";
	}
	return iml.removeEmptyObjects(iml.removeEmptyArrays(iml.removeNull(obj)));
}
