getClientId.code:
function getClientId(connectionDomain, dataClient, commonClientCOM, commonClientEU) {
	if (dataClient) {
		return dataClient
	}
	if (!connectionDomain) {
		connectionDomain = 'accounts.zoho.com'
	}
	if (connectionDomain === 'accounts.zoho.eu') {
		return commonClientEU || commonClientCOM //our prod oauth credential (.com) is available for any region
	}
	return commonClientCOM
}

buildCriteria.code:
function buildCriteria(filter) {
  if (!filter || !filter[0]) {
    return undefined;
  }
  const filters = [];
  (filter[0] || []).forEach((set) => {
    let row = "(" + set.a + ":";
    if (set.o === "text:startwith") {
      row += "starts_with";
    } else {
      row += "equals";
    }
    if (set.b && typeof set.b.getMonth === "function") {
      const [date, time] = set.b.toISOString().split("T");
      const [hour, min, last] = time.split(/:/g);
      const [sec] = last.split(".");
      row += ":" + `${date}T${hour}:${min}:${sec}Z` + ")";
    } else {
      row += ":" + iml.encodingSearchValue(set.b) + ")";
    }
    filters.push(row);
  });
  return "(" + filters.join(" and ") + ")";
}

