getObjectExpect.code:
function getObjectExpect(fields, layouts, type, isSubform, subformName, subformLabel) {
	const required = {}
	;(layouts || []).forEach(layout => {
		;(layout.sections || []).forEach(section => {
			;(section.fields || []).forEach(field => {
				required[field.api_name] = field.required
			})
		})
	})
	let seModule = false
	const data = (fields || [])
	.filter(field => field.view_type[type])
	// no API docs
	.filter(field => {
		if (field.api_name === 'Participants') {
			return false
		}
		if (field.data_type === 'RRULE' || field.data_type === 'event_reminder' || field.data_type === 'profileimage') {
			return false
		}
		/*
		if (field.field_read_only !== null && field.field_read_only !== undefined) {
			if (field.field_read_only) {
				return false
			}
		}
		*/
		return true
	})
	.map(field => {
		const value = {
			name: field.api_name,
			label: field.field_label,
			required: Boolean(required[field.api_name]),
			advanced: iml.if(isSubform, undefined, !Boolean(required[field.api_name])),
			editable: true,
			type: 'text'
		}

		if (field.api_name === 'Who_Id') {
			value.required = false
		}
		if (field.json_type === 'jsonarray'){
			value.type = 'array';
			if (field.data_type === 'subform') {
				return;
				//currently it's not possible to add RPC parameters on IML function :(
				/* if(field.subform && field.subform.module) {
					return `rpc://listSubformFields?module=${field.subform.module}&field_label=${field.field_label}`;
				}
				else return `rpc://listSubformFields?module=${field.api_name}&field_label=${field.field_label}`; */
			}
			if (field.api_name === 'Product_Details') {
				value.spec = [
					{
						name: 'product',
						label: 'Product',
						type: 'collection',
						spec: [
							{
								name: 'id',
								label: 'Product ID',
								type: 'text',
								required: true
							}
						]
					},
					{
						name: 'quantity',
						label: 'Quantity',
						type: 'number',
						required: true
					},
					{
						name: 'Discount',
						label: 'Discount',
						type: 'text',
						required: false
					},
					{
						name: 'list_price',
						label: 'List price',
						type: 'number',
						required: false
					}
				]
			}
			if (field.api_name === 'Pricing_Details') {
				value.spec = [
					{
						name: 'from_range',
						label: 'From range',
						type: 'number',
						required: true
					},
					{
						name: 'to_range',
						label: 'To range',
						type: 'number',
						required: true
					},
					{
						name: 'discount',
						label: 'Discount',
						type: 'number',
						required: true
					}
				]
			}
		}
		if (type === 'edit') {
			value.required = false
		}
		if (field.length) {
			value.validate = {
				max: field.length
			}
		}
		switch (field.data_type) {
			case 'RRULE': {
				value.help = 'Set RRULE.'
				break
			}
			case 'ownerlookup':
			case 'lookup': {
				value.type = 'text'
				value.help = 'Set ID.'
				if (field.api_name === 'What_Id') {
					value.help = 'Set ID. When this field contains a value, the field SE Module is required.'
				}
				break
			}
			case 'textarea': {
				value.multiline = true
				break
			}
			case 'website': {
				value.type = 'url'
				break
			}
			case 'picklist': {
				value.editable = true
				value.validate = false
				value.type = 'select'
				value.options = (field.pick_list_values || []).map(val => {
					return {
						label: val.display_value,
						value: val.display_value
					}
				})
				break
			}
			case 'multiselectpicklist': {
				value.type = 'select'
				value.multiple = true
				value.validate = false
				value.options = (field.pick_list_values || []).map(val => {
					return {
						label: val.display_value,
						value: val.actual_value
					}
				})
				break
			}
			case 'email': {
				value.type = 'email'
				break
			}
			case 'boolean': {
				value.type = 'boolean'
				value.editable = true
				break
			}
			case 'integer':
			case 'currency': {
				value.type = 'number'
				if (value.validate) {
					delete value.validate
				}
				break
			}
			case 'date': {
				value.type = 'date'
				value.time = false
				break
			}
			case 'ALARM':
			case 'datetime': {
				value.type = 'date'
				value.time = true
				break
			}
		}
		if (field.data_type === 'lookup' && field.lookup && field.lookup.module === 'se_module') {
			seModule = true
		}

		return value

	})
	if (seModule) {
		data.push({
			name: '$se_module',
			label: 'SE module',
			type: 'text',
			required: false
		})
	}
	if(isSubform) {
		return {
