function getResponseError(statusCode, body) {
	if (statusCode >= 400) {
		return `${body.message} (${body.code})`
	}
	if (statusCode === 202 && body && body.data && body.data[0] && body.data[0].status === 'error') {
		const row = body.data[0]
		return `${row.message} (${row.code}): ${JSON.stringify(row.details)}.`
	}
	return `Error: ${statusCode}: ${JSON.stringify(body)}.`
}

