function getObjectInsertInput(params, metadata, timezone, isSubform) {
	const meta = {}
	;(metadata || []).forEach(field => {
		meta[field.name] = field
	})

	const row = Object.assign({}, params)
	delete row.module
	Object.keys(row).forEach(key => {
		if (row[key] == null || row[key] === '') {
			delete row[key]
			return
		}
		if (Array.isArray(row[key])) {
			if(!row[key].length) {
				delete row[key]
				return
			}
			let arr_meta = metadata.filter(i => i.name === key);
			if(arr_meta[0].spec && Array.isArray(arr_meta[0].spec)) {
				row[key] = row[key].map(item => {
					item = iml.getObjectInsertInput(item, arr_meta[0].spec, timezone, true);
					return item;
				});
			}
			else return;
		}

		if (key === 'Product_Details') {
			Object.keys(row[key]).forEach(k => {
				if (row[key][k].Discount == null) {
					delete row[key][k].Discount
				}
				if (row[key][k].list_price == null) {
					delete row[key][k].list_price
				}
			})
		}
		if (typeof row[key].getMonth === 'function') {
			if (meta[key] && meta[key].time) {
				// date and time
				const [date, time] = row[key].toISOString().split('T')
				const [hour, min, last] = time.split(/:/g)
				const [sec] = last.split('.')
				row[key] = `${date}T${hour}:${min}:${sec}Z`
			} else {
				// only date
				row[key] = iml.formatDate(row[key], 'YYYY-MM-DD', timezone)
			}
		}
	})
	// row.se_module = 'Contacts'

	if(isSubform) return row;

	return {
		trigger: params.trigger,
		data: [row]
	}
}

